components/common/paginator:
{{#and
    (if show_dropdown)
    (if (length links) '>' 1)
}}
    {{#or (if page_type 'category') (if page_type 'search')}}
        {{> components/custom/paginator-dropdown page_count=paginator_dropdown_page_count}}
    {{else}}
        {{> components/custom/paginator-dropdown page_count=(get 'number' (last links))}}
    {{/or}}
{{else}}
    {{#or (if (length links) '>' 1) (if next) (if prev)}}
        <nav class="pagination" aria-label="pagination">
            <ul class="pagination-list">
                {{#if previous}}
                    <li class="pagination-item pagination-item--previous">
                        <a class="pagination-link"
                        href="{{previous}}"
                        {{#unless reload}}data-faceted-search-facet{{/unless}}
                        aria-label="{{lang 'common.previous'}}"
                        >
                            <i class="icon" aria-hidden="true">
                                <svg>
                                    <use xmlns:xlink="http://www.w3.org/1999/xlink" xlink:href="#icon-chevron-left"></use>
                                </svg>
                            </i>
                            {{lang 'common.previous'}}
                        </a>
                    </li>
                {{/if}}

                {{#each links}}
                    <li class="pagination-item {{#if this.number '==' ../current}}pagination-item--current{{/if}}">
                        <a class="pagination-link"
                        href="{{this.url}}"
                        {{#unless reload}}data-faceted-search-facet{{/unless}}
                        {{#if this.number '==' ../current}}
                        aria-current="page"
                        data-pagination-current-page-link
                        {{/if}}
                        aria-label="{{lang 'common.paginator.page_of' current=this.number total=../links.length}}"
                        >
                            {{this.number}}
                        </a>
                    </li>
                {{/each}}

                {{#if next}}
                    <li class="pagination-item pagination-item--next">
                        <a class="pagination-link"
                        href="{{next}}"
                        {{#unless reload}}data-faceted-search-facet{{/unless}}
                        aria-label="{{lang 'common.next'}}"
                        >
                            {{lang 'common.next'}}
                            <i class="icon" aria-hidden="true">
                                <svg>
                                    <use xmlns:xlink="http://www.w3.org/1999/xlink" xlink:href="#icon-chevron-right"></use>
                                </svg>
                            </i>
                        </a>
                    </li>
                {{/if}}
            </ul>
        </nav>
    {{/or}}
{{/and}}

components/custom/category-list-view:
{{#if category.products}}
    {{> components/custom/category-view-toggle type='list'}}
    <a class="category__compare-link" href="{{urls.compare}}" data-compare-nav
        aria-label="{{lang 'common.compare'}}">
        {{lang 'common.compare'}} <span class="countPill countPill--positive countPill--alt"></span>
    </a>
    {{> components/products/filter sort=pagination.category.sort}}

    <form action="{{urls.compare}}" method='POST' {{#if settings.data_tag_enabled}} data-list-name="Category: {{category.name}}" {{/if}} data-product-compare>
            {{#if settings.data_tag_enabled}}
                {{> components/products/list products=category.products show_compare=category.show_compare theme_settings=theme_settings event="list" }}
            {{else}}
                {{> components/products/list products=category.products show_compare=category.show_compare theme_settings=theme_settings}}
            {{/if}}
    </form>

    {{> components/common/paginator
        pagination.category
        page_type=page_type
        show_dropdown=theme_settings.its.paginator_dropdown
        paginator_dropdown_page_count=(ceil (divide category.total_products theme_settings.categorypage_products_per_page))
    }}
{{else}}
    <span role="text" tabindex="0" class="category__no-products">{{lang 'categories.no_products'}}</span>
{{/if}}

components/products/list-item:
{{#if settings.data_tag_enabled}}
    <article class="listItem"
            data-event-type="{{event}}"
            data-entity-id="{{id}}"
            data-position="{{position}}"
            data-name="{{name}}"
            data-product-category="{{#each category}}{{#if @last}}{{this}}{{else}}{{this}}, {{/if}}{{/each}}"
            data-product-brand="{{brand.name}}"
            data-product-price="{{#or customer (unless theme_settings.restrict_to_login)}}{{#if price.with_tax}}{{price.with_tax.value}}{{else}}{{price.without_tax.value}}{{/if}}{{else}}{{lang 'common.login_for_pricing'}}{{/or}}"
    >
{{else}}
    <article class="listItem">
{{/if}}
    <figure class="listItem-figure">
        <a href="{{url}}"
           class="listItem-figure__link"
           aria-label="{{> components/products/product-info}}"
           {{#if settings.data_tag_enabled}}
           data-event-type="product-click"
           {{/if}}
           {{#if theme_settings.its.image_swap_on_hover}} data-image-swap-link {{/if}}
        >
            {{#if stock_level '===' 0}}
                {{#if theme_settings.pdp_sold_out_label '===' ''}}
                    {{> components/products/product-badge
                        badge-type='sold-out'
                        badge_view=theme_settings.product_sold_out_badges
                        badge_label=(lang "page_builder.pdp_sold_out_label")
                    }}
                {{else}}
                    {{> components/products/product-badge
                        badge-type='sold-out'
                        badge_view=theme_settings.product_sold_out_badges
                        badge_label=theme_settings.pdp_sold_out_label
                    }}
                {{/if}}
            {{else}}
                {{#or price.sale_price_with_tax.value price.sale_price_without_tax.value}}
                    {{#if theme_settings.pdp_sale_badge_label '===' ''}}
                        {{> components/products/product-badge
                            badge-type='sale'
                            badge_view=theme_settings.product_sale_badges
                            badge_label=(lang "page_builder.pdp_sale_badge_label")
                        }}
                    {{else}}
                        {{> components/products/product-badge
                            badge-type='sale'
                            badge_view=theme_settings.product_sale_badges
                            badge_label=theme_settings.pdp_sale_badge_label
                        }}
                    {{/if}}
                {{/or}}
            {{/if}}
            <div class="list-img-container" {{#if theme_settings.its.image_swap_on_hover}} data-image-swap-src="{{#each images}}{{#if @index '===' 1}}{{getImage data 'productgallery_size'}}{{/if}}{{/each}}"{{/if}}>
                {{> components/common/responsive-img
                    image=image
                    class="listItem-image"
                    fallback_size=theme_settings.productgallery_size
                    lazyload=theme_settings.lazyload_mode
                    default_image=theme_settings.default_image_product
                }}
            </div>
        </a>
        {{#unless hide_product_quick_view}}
            {{#if theme_settings.show_product_quick_view}}
                <div class="listItem-figureBody">
                    <button type="button" {{#if settings.data_tag_enabled}} data-event-type="product-click" {{/if}} class="button button--small listItem-button quickview" data-product-id="{{id}}">{{lang 'products.quick_view'}}</button>
                </div>
            {{/if}}
        {{/unless}}
    </figure>
    <div class="listItem-body">
        <div class="listItem-content">
            <div class="listItem-details">
                {{#if show_rating}}
                    <p class="listItem-rating">{{> components/products/ratings rating_target=name rating=rating}}</p>
                {{/if}}
                {{#if brand.name}}
                <p class="listItem-brand">{{brand.name}}</p>
                {{/if}}
                <h4 class="listItem-title">
                    <a href="{{url}}"
                        aria-label="{{> components/products/product-info}}"
                        {{#if settings.data_tag_enabled}} data-event-type="product-click" {{/if}}
                    >
                        {{name}}
                    </a>
                </h4>
                {{#if summary}}
                    <p>{{{summary}}}</p>
                {{/if}}
            </div>
            <div class="listItem-actions">

                {{#or customer (if theme_settings.restrict_to_login '!==' true)}}
                    {{#if price}}
                        <div class="listItem-price">{{> components/products/price price=price bdsp_search_items=category}}</div>
                    {{/if}}
                    {{#and
                        (if theme_settings.its.card_atc_button)
                        (if theme_settings.its.card_atc_button_pos '===' 'cornerstone')
                    }}
                    {{#if show_cart_action}}
                        {{#if has_options}}
                            <a href="{{url}}" {{#if settings.data_tag_enabled}} data-event-type="product-click" {{/if}} class="button button--small" data-product-id="{{id}}">{{lang 'products.choose_options'}}</a>
                        {{/if}}
                        {{#if pre_order}}
                            <a href="{{pre_order_add_to_cart_url}}" data-event-type="product-click" class="button button--primary">{{lang 'products.pre_order'}}</a>
                        {{/if}}
                        {{#if add_to_cart_url }}
                            <a href="{{add_to_cart_url}}" {{#if settings.data_tag_enabled}} data-event-type="product-click" {{/if}} class="button button--primary">{{lang 'products.add_to_cart'}}</a>
                        {{/if}}
                        {{#if out_of_stock_message }}
                            <a href="{{url}}" data-event-type="product-click" class="button button--small" data-product-id="{{id}}">{{out_of_stock_message}}</a>
                        {{/if}}
                    {{/if}}
                    {{else}}
                        {{> components/custom/card-add-to-cart }}
                    {{/and}}
                {{else}}
                    {{> components/common/login-for-pricing}}
                {{/or}}
                {{#if show_compare}}
                    <label class="button button--small" for="compare-{{id}}">
                        {{lang "products.compare"}} <input type="checkbox" name="products[]" value="{{id}}" id="compare-{{id}}" data-compare-id="{{id}}">
                    </label>
                {{/if}}
            </div>
        </div>
    </div>
</article>

components/products/price:
{{!-- There are 2 code paths for generating the PDP pricing HTML determined by the following #and condition.
If a 'price_range' exists then we generate all HTML in price-range.html, otherwise it is defined here. Both code
paths generate the same HTML structure with some differences in whether that element is displayed by default (css styling).

If you are making a change here or in price-range.html, you probably want to make that change in both files. --}}


{{assignVar 'bdsp-percentageOff' 0}}

{{#each bdsp_search_items}}
    {{#contains this "Boxing Day Sale/"}}
        {{assignVar 'bdsp-amount' (toInt (last (split this '/')))}}

        {{#if (getVar 'bdsp-percentageOff') '<' (getVar 'bdsp-amount')}}
            {{assignVar 'bdsp-percentageOff' (getVar 'bdsp-amount')}}
        {{/if}}
    {{/contains}}
{{/each}}

{{#if (getVar 'bdsp-percentageOff') '!==' 0}}
    {{assignVar 'bdsp-amountOff' (multiply price.without_tax.value (divide (getVar 'bdsp-percentageOff') 100))}}
    {{assignVar 'bdsp-totalValue' (toFloat (toFixed (subtract price.without_tax.value (getVar 'bdsp-amountOff')) 2))}}

    <div class="price-section price-section--withoutTax rrp-price--withoutTax h4" style="display: none;">
        <span class="price-label is-srOnly">
            MSRP:
        </span>
        <span class="price price--rrp">
            {{price.rrp_without_tax.formatted}}
        </span>
    </div>
    <div class="price-section price-section--withoutTax non-sale-price--withoutTax {{#if page_type '!==' 'product'}}h5{{else}}h4{{/if}}">
        <span class="price-label is-srOnly">
            Price:
        </span>
        <span class="price price--non-sale">
            {{price.without_tax.formatted}}
        </span>
    </div>
    <div class="price-section price-section--withoutTax h4">
        <span class="price-label is-srOnly">
            Sale Price:
        </span>
        <span class="price price--withoutTax">
            ${{getVar 'bdsp-totalValue'}}
        </span>
    </div>
{{else}}

    {{#and price.price_range (if theme_settings.price_ranges '==' true)}}
        {{> components/products/price-range price=price}}
    {{else}}
        {{#if price.with_tax}}
            <div class="price-section price-section--withTax rrp-price--withTax h4" {{#unless price.rrp_with_tax}}style="display: none;"{{/unless}}>
                <span class="is-srOnly">
                    {{> components/products/price-label
                        label_value=theme_settings.pdp-retail-price-label
                        fallback=(lang "page_builder.pdp-retail-price-label")
                    }}
                </span>
                <span data-product-rrp-with-tax class="price price--rrp ">
                    {{price.rrp_with_tax.formatted}}
                </span>
            </div>
            <div class="price-section price-section--withTax non-sale-price--withTax {{#if page_type '!==' 'product'}}h5{{else}}h4{{/if}}" {{#unless price.non_sale_price_with_tax}}style="display: none;"{{/unless}}>
                <span class="is-srOnly">
                    {{> components/products/price-label
                        label_value=theme_settings.pdp-non-sale-price-label
                        fallback=(lang "page_builder.pdp-non-sale-price-label")
                    }}
                </span>
                <span data-product-non-sale-price-with-tax class="price price--non-sale">
                    {{price.non_sale_price_with_tax.formatted}}
                </span>
            </div>
            <div class="price-section price-section--withTax h4">
                <span class="price-label is-srOnly" {{#if price.non_sale_price_with_tax}}style="display: none;"{{/if}}>
                    {{theme_settings.pdp-price-label}}
                </span>
                <span class="price-now-label is-srOnly" {{#unless price.non_sale_price_with_tax}}style="display: none;"{{/unless}}>
                    {{> components/products/price-label
                        label_value=theme_settings.pdp-sale-price-label
                        fallback=(lang "page_builder.pdp-sale-price-label")
                    }}
                </span>
                <span data-product-price-with-tax class="price price--withTax">{{price.with_tax.formatted}}</span>
                {{#if price.without_tax}}
                    <abbr title="{{lang 'products.including_tax'}}">{{lang 'products.price_with_tax' tax_label=price.tax_label}}</abbr>
                {{/if}}
            </div>
        {{/if}}
        {{#if price.without_tax}}
            <div class="price-section price-section--withoutTax rrp-price--withoutTax{{#if price.with_tax}} price-section--minor{{/if}} h4" {{#unless price.rrp_without_tax}}style="display: none;"{{/unless}}>
                <span class="is-srOnly">
                    {{> components/products/price-label
                        label_value=theme_settings.pdp-retail-price-label
                        fallback=(lang "page_builder.pdp-retail-price-label")
                    }}
                </span>
                <span data-product-rrp-price-without-tax class="price price--rrp {{#if page_type '!==' 'product'}}h5{{else}}h4{{/if}}">
                    {{price.rrp_without_tax.formatted}}
                </span>
            </div>
            <div class="price-section price-section--withoutTax non-sale-price--withoutTax{{#if price.with_tax}} price-section--minor{{/if}} {{#if page_type '!==' 'product'}}h5{{else}}h4{{/if}}" {{#unless price.non_sale_price_without_tax}}style="display: none;"{{/unless}}>
                <span class="is-srOnly">
                    {{> components/products/price-label
                        label_value=theme_settings.pdp-non-sale-price-label
                        fallback=(lang "page_builder.pdp-non-sale-price-label")
                    }}
                </span>
                <span data-product-non-sale-price-without-tax class="price price--non-sale">
                    {{price.non_sale_price_without_tax.formatted}}
                </span>
            </div>
            <div class="price-section price-section--withoutTax h4">
                <span class="price-label is-srOnly" {{#if price.non_sale_price_without_tax}}style="display: none;"{{/if}}>
                    {{theme_settings.pdp-price-label}}
                </span>
                <span class="price-now-label is-srOnly" {{#unless price.non_sale_price_without_tax}}style="display: none;"{{/unless}}>
                    {{> components/products/price-label
                        label_value=theme_settings.pdp-sale-price-label
                        fallback=(lang "page_builder.pdp-sale-price-label")
                    }}
                </span>
                <span data-product-price-without-tax class="price price--withoutTax{{#if price.with_tax}} price-section--minor{{/if}}">{{price.without_tax.formatted}}</span>
                {{#if price.with_tax}}
                    <abbr title="{{lang 'products.excluding_tax'}}">{{lang 'products.price_without_tax' tax_label=price.tax_label}}</abbr>
                {{/if}}
            </div>
        {{/if}}
        {{!-- {{#if page_type '===' 'product'}}
            <div class="price-section price-section--saving price h4" {{#unless price.saved}}style="display: none;"{{/unless}}>
                <span class="price">{{lang 'products.you_save_opening_text'}}</span>
                <span data-product-price-saved class="price price--saving">
                    {{price.saved.formatted}}
                </span>
                <span class="price">{{lang 'products.you_save_closing_bracket'}}</span>
            </div>
        {{/if}} --}}
    {{/and}}
{{/if}}

components/products/price-range:
{{#and price.retail_price_range.min.with_tax price.retail_price_range.max.with_tax}}
    <div class="price-section price-section--withTax rrp-price--withTax">
        <span>
            {{> components/products/price-label
                label_value=theme_settings.pdp-retail-price-label
                fallback=(lang "page_builder.pdp-retail-price-label")
            }}
        </span>
        <span data-product-rrp-price-with-tax class="price price--rrp">{{price.retail_price_range.min.with_tax.formatted}} - {{price.retail_price_range.max.with_tax.formatted}}</span>
    </div>
{{else}}
    {{#if price.with_tax}}
        <div class="price-section price-section--withTax rrp-price--withTax" style="display: none;">
            <span>
                {{> components/products/price-label
                    label_value=theme_settings.pdp-retail-price-label
                    fallback=(lang "page_builder.pdp-retail-price-label")
                }}
            </span>
            <span data-product-rrp-with-tax class="price price--rrp">
                {{price.rrp_with_tax.formatted}}
            </span>
        </div>
    {{/if}}
{{/and}}
{{#and price.price_range.min.with_tax price.price_range.max.with_tax}}
    {{!-- Never display the "non-sales price" if there is a price range to be shown, but we do want the element on the page --}}
    <div class="price-section price-section--withTax non-sale-price--withTax" style="display: none;">
        <span>
            {{> components/products/price-label
                label_value=theme_settings.pdp-non-sale-price-label
                fallback=(lang "page_builder.pdp-non-sale-price-label")
            }}
        </span>
        <span data-product-non-sale-price-with-tax class="price price--non-sale">
            {{price.non_sale_price_with_tax.formatted}}
        </span>
    </div>
    <div class="price-section price-section--withTax">
        <span class="price-label">{{theme_settings.pdp-price-label}}</span>
        <span class="price-now-label" style="display: none;">
            {{> components/products/price-label
                label_value=theme_settings.pdp-sale-price-label
                fallback=(lang "page_builder.pdp-sale-price-label")
            }}
        </span>
        <span data-product-price-with-tax class="price price--withTax">{{price.price_range.min.with_tax.formatted}} - {{price.price_range.max.with_tax.formatted}}</span>
        {{#and price.price_range.min.without_tax price.price_range.max.without_tax}}
            <abbr title="{{lang 'products.including_tax'}}">{{lang 'products.price_with_tax' tax_label=price.price_range.min.tax_label}}</abbr>
        {{/and}}
    </div>
{{/and}}
{{#and price.retail_price_range.min.without_tax price.retail_price_range.max.without_tax}}
    <div class="price-section price-section--withoutTax rrp-price--withoutTax{{#if price.with_tax}} price-section--minor{{/if}} price-range h4">
        <span>
            {{> components/products/price-label
                label_value=theme_settings.pdp-retail-price-label
                fallback=(lang "page_builder.pdp-retail-price-label")
            }}
        </span>
        <span data-product-rrp-price-without-tax class="price price--rrp">{{price.retail_price_range.min.without_tax.formatted}} - {{price.retail_price_range.max.without_tax.formatted}}</span>
    </div>
{{else}}
    {{#if price.without_tax}}
        <div class="price-section price-section--withoutTax rrp-price--withoutTax{{#if price.with_tax}} price-section--minor{{/if}} price-range" style="display: none;">
            <span>
                {{> components/products/price-label
                    label_value=theme_settings.pdp-retail-price-label
                    fallback=(lang "page_builder.pdp-retail-price-label")
                }}
            </span>
            <span data-product-rrp-price-without-tax class="price price--rrp">
                {{price.rrp_without_tax.formatted}}
            </span>
        </div>
    {{/if}}
{{/and}}
{{#and price.price_range.min.without_tax price.price_range.max.without_tax}}
    {{!-- Never display the "non-sales price" if there is a price range to be shown, but we do want the element on the page --}}
    <div class="price-section price-section--withoutTax non-sale-price--withoutTax{{#if price.with_tax}} price-section--minor{{/if}} price-range" style="display: none;">
        <span>
            {{> components/products/price-label
                label_value=theme_settings.pdp-non-sale-price-label
                fallback=(lang "page_builder.pdp-non-sale-price-label")
            }}
        </span>
        <span data-product-non-sale-price-without-tax class="price price--non-sale">
            {{price.non_sale_price_without_tax.formatted}}
        </span>
    </div>
    <div class="price-section price-section--withoutTax{{#and price_range.min.with_tax price_range.max.with_tax}} price-section--minor{{/and}} price-range">
        <span class="price-label">{{theme_settings.pdp-price-label}}</span>
        <span class="price-now-label" style="display: none;">
            {{> components/products/price-label
                label_value=theme_settings.pdp-sale-price-label
                fallback=(lang "page_builder.pdp-sale-price-label")
            }}
        </span>
        <span data-product-price-without-tax class="price price--withoutTax">{{price.price_range.min.without_tax.formatted}} - {{price.price_range.max.without_tax.formatted}}</span>
        {{#and price.price_range.min.with_tax price.price_range.max.with_tax}}
            <abbr title="{{lang 'products.excluding_tax'}}">{{lang 'products.price_without_tax' tax_label=price.price_range.min.tax_label}}</abbr>
        {{/and}}
    </div>
{{/and}}

{{!-- Never display the "saving price" element by default if there is a price range on the page to be shown --}}
{{#if page_type '===' 'product'}}
    <div class="price-section price-section--saving price" style="display: none;">
            <span class="price">{{lang 'products.you_save_opening_text'}}</span>
            <span data-product-price-saved class="price price--saving">
                {{price.saved.formatted}}
            </span>
            <span class="price">{{lang 'products.you_save_closing_bracket'}}</span>
    </div>
{{/if}}

