components/attributes/body-classes:
{{#contains page_type 'account'}}
    page--account
{{/contains}}

page--{{ page_type }}

{{#if page_type '===' 'category'}}
    category--{{category.id}}
{{else if page_type '===' 'product'}}
    product--{{product.id}}
{{else if page_type '===' 'page'}}
    page--{{#replace ' ' (dashcase page.title)}}{{else}}{{dashcase page.title}}{{/replace}}
{{/if}}

{{#if customer}}
    logged-in
    customer-group-{{#if customer_group_id}}{{customer_group_id}}{{else}}null{{/if}}
{{else}}
    not-logged-in
{{/if}}

{{#and (if theme_settings.its.kitchen_sink) (if in_development)}}
    show-kitchen-sink
{{/and}}

components/common/currency-selector:
{{#if currency_selector.currencies.length '>' 1}}

    <li class="navUser-item">
        <a class="navUser-action navUser-action--currencySelector has-dropdown"
           href="#"
           data-dropdown="currencySelection"
           aria-controls="currencySelection"
           aria-expanded="false"
        >
            <span class="currency-flag {{lang 'common.currency' code=currency_selector.active_currency_code}}">
                {{{currency_selector.active_currency_flag}}}
            </span>
            {{!lang 'common.currency' code=currency_selector.active_currency_code}}
            <i class="icon" aria-hidden="true">
                <svg>
                    <use xlink:href="#icon-chevron-down" />
                </svg>
            </i>
        </a>
        <ul class="dropdown-menu" id="currencySelection" data-dropdown-content aria-hidden="true" tabindex="-1">
            {{#each currency_selector.currencies}}
            <li class="dropdown-menu-item">
                <a class="dropdown-menu-link" href="{{{switch_url}}}">
                {{#if is_active}}
                {{{flag}}}<strong>{{code}}</strong>
                {{else}}
                {{{flag}}}{{code}}
                {{/if}}
                </a>
            </li>
            {{/each}}
        </ul>
    </li>

{{/if}}

components/common/navigation-list-altern:
{{#if children}}
    <a class="navPages-action navPages-action-depth-max has-subMenu is-root{{#if is_active}} activePage{{/if}}"
       href="{{url}}"
       data-collapsible="navPages-{{id}}"
       aria-label="{{name}}"
    >
        {{name}}
        <i class="icon navPages-action-moreIcon" aria-hidden="true">
            <svg><use xlink:href="#icon-chevron-down" /></svg>
        </i>
    </a>
    {{> components/common/navigation-dropdown}}
{{else}}
    <a class="navPages-action{{#if is_active}} activePage{{/if}}"
       href="{{url}}"
       aria-label="{{name}}"
    >
        {{name}}
    </a>
{{/if}}

