        <p class="pdp-banner__heading h2">{{lang 'custom.product.sold-out-banner.heading'}}</p>
        <p class="pdp-banner__text">{{{lang 'custom.product.sold-out-banner.text'}}}</p>
    </div>
{{/if}}

